package Array;

public class array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char name[]=new char[7];//declaration
		name[0]='A';//initialization
		name[1]='N';
		name[2]='U';
		name[3]=' ';
		name[4]='S';
		name[5]='R';
		name[6]='I';
		for(int i=0;i<=name.length-1;i++)
		{
			System.out.print(name[i]);
		}

	}

}
