package Controlstat;

public class Ifelseif {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int a=2,b=5;
		if(a>b)
		{
			System.out.println("a is greater than b");
		}
		else if(b>a)
		{
			System.out.println("b is greater than a");
		}
		else
		{
			System.out.println("a is equal to b");
		}

	}

}
